import React, { useCallback, useEffect, useRef, useState } from 'react';

/*
fetch('src/Data/SpeedSlalomRecord.json')
  .then(response => response.json())
  .then(data => console.log(data));
*/

interface Athlete {
  name: string;
  records: Record<string, any>[];
}

interface SpeedSlalomForm extends Athlete {
  records: [
    date: any, // 0
    side: "L" | "R",  // 1
    step: number, // 2
    time: number, // 3
    missedCone: number, // 4
    kickedCone: number, // 5
    DQ: boolean,  // 6
    endLine: boolean, // 7
    result: any,  // 8
    notes?: string  // 9
  ]
}

const SpeedSlalom = () => {
  const [athletes, setAthletes] = useState<Athlete[]>([]);
  const inputRef = useRef<HTMLInputElement>(null);

  const [SpeedSlalomForm, setSpeedSlalomForm] = useState<SpeedSlalomForm>({
    name: '',
    records: ['', 'L', 0, 0, 0, 0, false, false, 0, ''],
  });

  useEffect(() => {
    fetch('src/Data/SpeedSlalomForm.json')
      .then(response => response.json())
      .then(data => setAthletes(data.athletes));
  }, []);


  // Handle submit - SpeedSlalom form 
  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    // Calculate the result and keep 3 decimal places
    const result = (Number(SpeedSlalomForm.records[3]) + ((Number(SpeedSlalomForm.records[4]) + Number(SpeedSlalomForm.records[5])) * 0.2)).toFixed(3);

    console.log(SpeedSlalomForm);

    setSpeedSlalomForm({
      ...SpeedSlalomForm,
      records: SpeedSlalomForm.records.map((record, index) => {
        if (index === 8) {
          return result;
        }
        return record;
      }) as [date: any, side: "L" | "R", step: number, time: number, missedCone: number, kickedCone: number, DQ: boolean, endLine: boolean, result: any, notes?: string | undefined],
    });

    /*
        // get json from local 
        fetch('file:///src/Data/SpeedSlalomRecord.json')
          .then(response => response.json())
          .then(data => {
            // Update data with new form submission
            const updatedData = {
              ...data,
              athletes: [
                ...data.athletes,
                {
                  name: SpeedSlalomForm.name,
                  records: [
                    {
                      date: SpeedSlalomForm.date,
                      side: SpeedSlalomForm.side,
                      step: SpeedSlalomForm.step,
                      time: SpeedSlalomForm.time,
                      missedCone: SpeedSlalomForm.missedCone,
                      kickedCone: SpeedSlalomForm.kickedCone,
                      DQ: SpeedSlalomForm.DQ,
                      endLine: SpeedSlalomForm.endLine,
                      result: result,
                      notes: SpeedSlalomForm.notes,
                    },
                  ],
                },
              ],
            };
    
            // Write updated data back to JSON file
            fetch('file:///src/Data/SpeedSlalomRecord.json', {
              method: 'PUT',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify(updatedData),
            })
              .then(response => response.json())
              .then(data => console.log(data))
              .catch(error => console.error(error));
          })
          .catch(error => console.error(error));
    */
  };

  // Handle change - SppedSlalom form  
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSpeedSlalomForm({ ...SpeedSlalomForm, [event.target.name]: event.target.value });

    if (inputRef.current) {
      inputRef.current.value = event.target.value;
    }

    const index = parseInt(event.target.name.replace('record', ''));

    const newRecords = [...SpeedSlalomForm.records];
    newRecords[index] = event.target.value;
    setSpeedSlalomForm({
      ...SpeedSlalomForm,
      records: newRecords as [date: any, side: "L" | "R", step: number, time: number, missedCone: number, kickedCone: number, DQ: boolean, endLine: boolean, result: any, notes?: string | undefined],
    });

    if (event.target.name === 'endline') {
      setSpeedSlalomForm({
        ...SpeedSlalomForm,
        records: SpeedSlalomForm.records.map((record, index) => {
          if (index === 7) {
            return event.target.checked;
          }
          return record;
        }) as [date: any, side: "L" | "R", step: number, time: number, missedCone: number, kickedCone: number, DQ: boolean, endLine: boolean, result: any, notes?: string | undefined],
      });
    } else {
      setSpeedSlalomForm({ ...SpeedSlalomForm, [event.target.name]: event.target.value });
    }
  };

  // get data from speedslalomrecords
  const [SSRecords, setSSRecords] = useState([]);
  useEffect(() => {

    const fetchData = async () => {
      try {
        const res = await fetch('http://localhost:8001/getSSRecords')
        if (!res.ok) {
          throw new Error(`HTTP error! status: ${res.status}`);
        }
        const data = await res.json()
        setSSRecords(data.speedslalomrecords)
        console.log(data)
      } catch (error) {
        console.log(error)
      }
    }
    fetchData()
  }, [])

  // Calculate and test the output
  /*
  const Cal = () => {
    const result = (Number(SpeedSlalomForm.time) + ((Number(SpeedSlalomForm.missedCone) + Number(SpeedSlalomForm.kickedCone)) * 0.2)).toFixed(3);
    return result;
  }
  */

  return (
    <div className='main'><h1 className='tittle'>Speed Slalom</h1>
      <form onSubmit={handleSubmit} className='form'>
        <label>
          Name: <input type="text" ref={inputRef} name="name" id="inputType" value={SpeedSlalomForm.name} onChange={handleChange} />
        </label>
        <br />

        {/*
        <label>
          Date: <input type="date" ref={inputRef} name="date" id="inputType" value={SpeedSlalomForm.records[0] || new Date().toISOString().split('T')[0]} onChange={handleChange} />
        </label>
        <br />
        <label>
          Side: <input type="text" ref={inputRef} name="side" id="inputType" value={SpeedSlalomForm.records[1]} onChange={handleChange} />
        </label>
        <br />
        <label>
          Step: <input type="number" ref={inputRef} name="step" id="inputType" value={SpeedSlalomForm.records[2]} onChange={handleChange} />
        </label>
        <br />
        <label>
          Time: <input type="number" ref={inputRef} name="time" id="inputType" value={SpeedSlalomForm.records[2]} onChange={handleChange} />
        </label>
        <br />
        <label>
          Missed: <input type="number" ref={inputRef} name="missedCone" id="inputType" value={SpeedSlalomForm.records[4] == null ? 0 : SpeedSlalomForm.records[4]} onChange={handleChange} />
        </label>
        <br />
        <label>
          Kicked: <input type="number" ref={inputRef} name="kickedCone" id="inputType" value={SpeedSlalomForm.records[5] == null ? 0 : SpeedSlalomForm.records[5]} onChange={handleChange} />
        </label>
        <br />
*/}


<input type="text" name="record0" value={SpeedSlalomForm.records[0]} onChange={handleChange} />
<input type="text" name="record1" value={SpeedSlalomForm.records[1]} onChange={handleChange} />
<input type="text" name="record2" value={SpeedSlalomForm.records[2]} onChange={handleChange} />
<input type="text" name="record3" value={SpeedSlalomForm.records[3]} onChange={handleChange} />
<input type="text" name="record4" value={SpeedSlalomForm.records[4]} onChange={handleChange} />
<input type="text" name="record5" value={SpeedSlalomForm.records[5]} onChange={handleChange} />


        <label>
          End line: <input type="checkbox" ref={inputRef} name="endline" id="inputType" checked={SpeedSlalomForm.records[7]} onChange={handleChange} />
        </label>
        <br />
        <label>{/* checkbox default true? */}
          <p>DQ: {Number(SpeedSlalomForm.records[5]) + Number(SpeedSlalomForm.records[4]) > 4 || SpeedSlalomForm.records[7] ? 'Yes' : 'No'}</p>
        </label>
        <br />
        <button type="submit">Submit</button>
        <br />
        <p>
          Result:  <input type="text" name="result" id="inputType" value={SpeedSlalomForm.records[8]} onChange={handleChange} disabled />
        </p>
        <br />
        {/* to test output
        <p>{Cal()}</p>
        */}
      </form>

      {/*SSRecords.map((SSRecords, index) => (<p>{SSRecords[0]}, {SSRecords[1]}</p>))*/}
      {SSRecords.map((record: { name: string, records: any[] }, i: React.Key) => (
        <div key={i}>
          <h2>{record.name}</h2>
          <ul>
            {record.records.map((r: any, i: React.Key) => (
              <li key={i}>
                <p>Date: {r.date}</p>
                <p>Side: {r.side}</p>
                <p>Step: {r.step}</p>
                <p>Time: {r.time}</p>
                <p>Missed Cone: {r.missedCone}</p>
                <p>Kick Cone: {r.kickedCone}</p>
                <p>DQ: {r.DQ ? 'Yes' : 'No'}</p>
                <p>End Line: {r.endLine ? 'Yes' : 'No'}</p>
                <p>Result: {r.result}</p>
                <p>Notes: {r.notes}</p>
              </li>
            ))}
          </ul>
        </div>
      ))}

    </div>
  );
};

export default SpeedSlalom;
