import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { SpeedSlalomBasic } from '../Model/Interface';

const host = 'localhost:3001';


const AnalyzeSSR = () => {

  interface athletesData {
    [key: string]: {
      min: number;
      max: number;
      avg: number;
    };
  }
  const [SSRecords, setSSRecords] = useState<SpeedSlalomBasic[]>([]);
  const [athletesData, setAthletesData] = useState<Record<string, any>>({});

  useEffect(() => {
    axios.get(`http://${host}/api/getSSRecords`)
      .then(response => {
        const SSRecords = response.data;
        const athletesData: Record<string, any> = {};

        SSRecords.forEach((record: SpeedSlalomBasic) => {
          const athleteName = record.AthleteName;
          if (!athletesData[athleteName]) {
            athletesData[athleteName] = {};
          }

          record.SSRecord.forEach((SSRecord) => {
            Object.keys(SSRecord).forEach((key) => {
              if (key !== 'date' && key !== 'side' && key !== 'step') {
                if (!athletesData[athleteName][key]) {
                  athletesData[athleteName][key] = [];
                }
                athletesData[athleteName][key].push(ssRecord[key]);
              }
            });
          });
        });

        Object.keys(athletesData).forEach((athleteName) => {
          Object.keys(athletesData[athleteName]).forEach((key) => {
            const values = athletesData[athleteName][key];
            const min = Math.min(...values);
            const max = Math.max(...values);
            const avg = values.reduce((a, b) => a + b, 0) / values.length;

            athletesData[athleteName][key] = { min, max, avg };
          });
        });

        setAthletesData(athletesData);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);
  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Athlete Name</th>
            <th>Min</th>
            <th>Max</th>
            <th>Average</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(athletesData).map((athleteName) => (
            <tr key={athleteName}>
              <td>{athleteName}</td>
              {Object.keys(athletesData[athleteName]).map((key) => (
                <td key={key}>
                  {athletesData[athleteName][key].min} ({key}.min)
                </td>
              ))}
              {Object.keys(athletesData[athleteName]).map((key) => (
                <td key={key}>
                  {athletesData[athleteName][key].max} ({key}.max)
                </td>
              ))}
              {Object.keys(athletesData[athleteName]).map((key) => (
                <td key={key}>
                  {athletesData[athleteName][key].avg} ({key}.avg)
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AnalyzeSSR;
