import React, { useState } from 'react';

interface AthleteForm {
  name: string;

}

interface SpeedSlalom extends AthleteForm {
  side: "L" | "R";
  step: number;
  time: number;
  missedCone: number;
  kickCone: number;

}

const App = () => {
  const [SpeedSlalom, setSpeedSlalom] = useState<SpeedSlalom>({ name: '', side: '' , step: 0, time: 0, missedCone: 0, kickCone: 0 });

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    console.log(SpeedSlalom);
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSpeedSlalom({ ...SpeedSlalom, [event.target.name]: event.target.value });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" name="name" value={SpeedSlalom.name} onChange={handleChange} />
      </label>
      <br />
      <label>
        Email:
        <input type="email" name="email" value={SpeedSlalom.email} onChange={handleChange} />
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default App;
