import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { SpeedSlalomBasic, SSRdetails } from '../Model/Interface';

const host = 'localhost:3001';

interface AthletesSSRecordsData {
  [athleteName: string]: SSRdetails[];
}

const AnalyzeSSR = () => {
  const [AthletesSSRecordsData, setAthletesSSRecordsData] = useState<AthletesSSRecordsData>({});

  const [showNormal, setShowNormal] = useState(true);
  const [showDetails, setShowDetails] = useState(false);

  const [sortKey, setSortKey] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');

  const getSSRecordsNormal = () => {
    setShowNormal(true);
    setShowDetails(false);
    axios.get(`http://${host}/api/getSSRecords?recordType=Normal`)
      .then(response => {
        setAthletesSSRecordsData(response.data);
        console.log(AthletesSSRecordsData.athleteName);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const getSSRecordsDetails = () => {
    setShowNormal(false);
    setShowDetails(true);
    axios.get(`http://${host}/api/getSSRecords?recordType=Details`)
      .then(response => {
        setAthletesSSRecordsData(response.data);
        console.log(AthletesSSRecordsData.athleteName);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleSort = (key: string) => {
    if (sortKey === key) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortKey(key);
      setSortOrder('asc');
    }
    if (key === 'athleteName') {
      const sortedRecords = Object.entries(AthletesSSRecordsData).sort((a, b) => {
        const aAthleteName = a[0];
        const bAthleteName = b[0];
        if (sortOrder === 'asc') {
          return aAthleteName.localeCompare(bAthleteName);
        } else {
          return bAthleteName.localeCompare(aAthleteName);
        }
      });
  
      setAthletesSSRecordsData(
        sortedRecords.reduce((acc, [athleteName, records]) => {
          acc[athleteName] = records;
          return acc;
        }, {})
      );
    }
  };

  const thElements = document.querySelectorAll('th[data-sort]');

  thElements.forEach((th) => {
    th.addEventListener('click', () => {
      const sortKey = th.getAttribute('data-sort') || '';
      handleSort(sortKey);
    });
  });

  const sortedRecords = Object.entries(AthletesSSRecordsData).map(([athleteName, records]) => {
    if (sortKey) {
      return [
        athleteName,
        records.sort((a, b) => {
          const aValue = a[sortKey as keyof SSRdetails];
          const bValue = b[sortKey as keyof SSRdetails];



          if (typeof aValue === 'number' && typeof bValue === 'number') {
            if (sortOrder === 'asc') {
              return aValue < bValue ? -1 : 1;
            } else {
              return aValue > bValue ? -1 : 1;
            }
          } else if (typeof aValue === 'string' && typeof bValue === 'string') {
            const aValueLower = aValue.toLowerCase();
            const bValueLower = bValue.toLowerCase();

            if (sortOrder === 'asc') {
              return aValueLower.localeCompare(bValueLower);
            } else {
              return bValueLower.localeCompare(aValueLower);
            }
          } else if (typeof aValue === 'boolean' && typeof bValue === 'boolean') {
            if (sortOrder === 'asc') {
              return aValue ? 1 : -1;
            } else {
              return aValue ? -1 : 1;
            }

          } else {
            throw new Error(`Invalid value type for sort key ${sortKey}`);
          }
        }),
      ];
    } else {
      return [athleteName, records];
    }
  });

  return (
    <div>
      <button onClick={getSSRecordsNormal}>Request Normal SSRecords</button>
      <button onClick={getSSRecordsDetails}>Request Details SSRecords</button>

      <table>
        <thead>
          <tr>
            <th>
              <button onClick={() => handleSort('athleteName')}>Athlete</button>
            </th>
            <th>
              <button onClick={() => handleSort('time')}>Time</button>
            </th>
            <th>
              <button onClick={() => handleSort('missedCone')}>Missed Cone</button>
            </th>
            <th>
              <button onClick={() => handleSort('kickedCone')}>Kicked Cone</button>
            </th>
            <th>
              <button onClick={() => handleSort('DQ')}>DQ</button>
            </th>
            <th>
              <button onClick={() => handleSort('endLine')}>End Line</button>
            </th>
            <th>
              <button onClick={() => handleSort('SSResult')}>SSResult</button>
            </th>
            <th>
              <button onClick={() => handleSort('notes')}>Notes</button>
            </th>
          </tr>
        </thead>
        {showNormal && (
          <tbody>
            {Object.entries(AthletesSSRecordsData).map(([athleteName, records]) => (
              records.map((SSRecord, recordIndex) => (
                <tr key={`${athleteName}-${recordIndex}`}>
                  <td>{athleteName}</td>
                  <td>{SSRecord.time}</td>
                  <td>{SSRecord.missedCone}</td>
                  <td>{SSRecord.kickedCone}</td>
                  <td>{SSRecord.DQ.toString()}</td>
                  <td>{SSRecord.endLine.toString()}</td>
                  <td>{SSRecord.SSResult}</td>
                  <td>{SSRecord.notes}</td>
                </tr>
              ))
            ))}
          </tbody>
        )}

        {showDetails && (
          <tbody>
            {Object.entries(AthletesSSRecordsData).map(([athleteName, records]) => (
              records.map((SSRecord, recordIndex) => (
                <tr key={`${athleteName}-${recordIndex}`}>
                  <td>{athleteName}</td>
                  <td>{SSRecord.time}</td>
                  <td>{SSRecord.missedCone}</td>
                  <td>{SSRecord.kickedCone}</td>
                  <td>{SSRecord.DQ.toString()}</td>
                  <td>{SSRecord.endLine.toString()}</td>
                  <td>{SSRecord.SSResult}</td>
                  <td>{SSRecord.notes}</td>
                </tr>
              ))
            ))}
          </tbody>
        )}
      </table>
    </div>
  );
}

export default AnalyzeSSR;
