import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { SpeedSlalomForm } from '../Model/Interface';

interface Props {
    speedSlalomRecords: SpeedSlalomForm[];
}

const SpeedSlalomRecordTable: React.FC<Props> = ({ speedSlalomRecords }) => {
    const [tableData, setTableData] = useState<{ athleteName: string; min: number; max: number; avg: number; }[]>([]);

    useEffect(() => {
        fetchSSRecords();
    }, []);

    const fetchSSRecords = async () => {
        try {
            const response = await axios.get('http://localhost:3001/api/getSSRecord');
            const data: SpeedSlalomForm[] = response.data;
            const athleteNames = Array.from(new Set(data.map((record) => record.AthleteName)));
            const tableData = athleteNames.map((athleteName) => {
                const athleteRecords = data.filter((record) => record.AthleteName === athleteName);
                const times = athleteRecords.map((record) => record.time).filter((time) => time !== null);
                console.log(athleteName, times);
                const min = Math.min(...times);
                const max = Math.max(...times);
                const avg = times.length > 0 ? times.reduce((sum, time) => sum + time, 0) / times.length : 0;
                return {
                    athleteName,
                    min,
                    max,
                    avg,
                };
            });
            setTableData(tableData);
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <table>
            <thead>
                <tr>
                    <th>Athlete Name</th>
                    <th>Min SSRecord</th>
                    <th>Max SSRecord</th>
                    <th>Avg SSRecord</th>
                </tr>
            </thead>
            <tbody>
                {tableData.map((row, index) => (
                    <tr key={index}>
                        <td>{row.athleteName}</td>
                        <td>{row.min.toFixed(3)}</td>
                        <td>{row.max.toFixed(3)}</td>
                        <td>{row.avg.toFixed(3)}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default SpeedSlalomRecordTable;
